// Get fund from users
// Withdraw funds
// Set a minimum funding value in USd

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;
import "./PriceConverter.sol";

error NotOwner();

contract FundMe {
    using PriceConverter for uint256;

    uint256 public constant minimumUsd = 50 * 1e18;
    // 21,371 constant
    // 23471 not constant
    address[] public funders;
    mapping(address => uint256) public addressToAmountFunded;

    address public immutable i_owner;
    AggregatorV3Interface public priceFeed;

    constructor(address priceFeedAddress) {
        i_owner = msg.sender;
        priceFeed = AggregatorV3Interface(priceFeedAddress);
    }

    // 837,393
    function fund() public payable {
        // want to be able to set a minimum fund amount in USD
        // 1. How do we send ETH to this contract
        // the value would be in *** wei ***
        // conversion from wei (18 dec.)  to usd (18dec.ï¼‰
        require(
            msg.value.getConversionRate(priceFeed) >= minimumUsd,
            "Does not have enough Eth"
        ); // 1e18 = 1*10**18 == 1000000000000000000 == ethereum
        // msg value will have 18 decimals
        funders.push(msg.sender);
        addressToAmountFunded[msg.sender] = msg.value;
    }

    function withdraw() public onlyOwner {
        for (
            uint256 funderIndex = 0;
            funderIndex < funders.length;
            funderIndex++
        ) {
            address funder = funders[funderIndex];
            addressToAmountFunded[funder] = 0;
        }
        // reset the array (0)=> 0 object
        funders = new address[](0);
        // // actually withdraw the fund
        // //transfer
        // payable(msg.sender).transfer(address(this).balance);
        // //send
        // bool sendSuccess = payable(msg.sender).send(address(this).balance);
        // require(sendSuccess, "Send Failed");
        // //call
        (bool callSuccess, ) = payable(msg.sender).call{
            value: address(this).balance
        }("");
        require(callSuccess, "Call Failed");
    }

    modifier onlyOwner() {
        // require(msg.sender == i_owner, "Sender is not the owner");
        if (msg.sender != i_owner) {
            revert NotOwner();
        }
        _; // All the code in the function, has to be after require
    }

    receive() external payable {
        fund();
    }

    fallback() external payable {
        fund();
    }
}

